import React, { useState } from 'react'
import PropTypes from 'prop-types'

// const index =(onAdd)=> {
const AddProduct = ({ onAdd }) => {
    const [value, setValue] = useState({});

    const onHandleChange = (e) => {
        const { value } = e.target;
        setValue({ name: value })
    }
    const onHandleSubmit = (e) => {
        e.preventDeFault();
        onAdd(value);
    }

return (
    <div>
        <div className="card shadow mb4">
            <div className="card-header py-3">
                <h6 className="m-0 font-weight-bold text-primary"> thêm sản phẩm</h6>
            </div>
            <div className="card-body">
                <form onSubmit={onHandleSubmit}>
                    <div className="form-group">
                        <label htmlFor="exampleInputEmil1">products</label>
                        <input type="text" className="form-control" id="exampleInput" onChange={onHandleChange} />

                    </div>
                    <button type="submit" className="btn btn-primary">Submit</button>
                </form>

            </div>
        </div>
    </div>
)
}

AddProduct.propTypes = {

}

export default AddProduct
