import React, { useState, useEffect } from 'react'
import PropTypes from 'prop-types';
import { useParams, useHistory } from 'react-router-dom';
import axios from 'axios';
import { useForm } from 'react-hook-form';


const EditProduct = () => {
    const { id } = useParams();
    let history = useHistory();

    const [products, setProduct] = useState({
        name: "",
        image: "",
        price: "",
    });

    const { name, image, price } = products;

    const onInputChange = e => {
        setProduct({
            ...products, [e.target.name]: e.target.value

        })
    }
    useEffect(() => {
        loadProduct();
    }, []);
    const loadProduct = async () => {
        const { data: { name, image, price } } = await axios.get(`http://localhost:8888/products/${id}`);
        setProduct({ name, price })
    }

    // const onSubmit = async e => {
    //     e.preventDefault();
    //     await axios.put(`http://localhost:8888/products/${id}`, products);
    //     history.push('/admin/products')
    // }
    const onHandleSubmit = (data) => {
        
        let file = data.image[0];
        // tạo reference chứa ảnh trên firesbase
        let storageRef = firebase.storage().ref(`images/${file.name}`);
        // đẩy ảnh lên đường dẫn trên
        storageRef.put(file).then(function () {
            storageRef.getDownloadURL().then((url) => {
                console.log(url);
                // Tạo object mới chứa toàn bộ thông tin từ input
                const newData = {
                    id: Math.random().toString(36).substr(2, 9),
                    ...data,
                    
                    image: url
                }
                console.log(newData);
                // đẩy dữ liệu ra ngoài app.js thông qua props onAdd
                onAdd(newData);
                history.push("/admin/products");
            })
        });
    }
    return (
        <div>
            <div className="container">
                <div className="w-75 mx-outo shadow p-5">
                    <h2 className="text-center mb-4">EditProduct</h2>
                    <form onSubmit={handleSubmit(onHandleSubmit)} >
                        <div className="form-group">
                            <label htmlFor="productName">Tên sản phẩm</label>
                            <input type="text" name="name" value={name} onChange={e => onInputChange(e)} className="form-control" />
                        </div>
                        <div className="form-group">
                            <label htmlFor="productPrice">Ảnh sản phẩm</label>
                            <div className="input-group">
                                <div className="custom-file">
                                    <input type="file" className="custom-file-input" id="inputGroupFile02" name="image" value={image} onChange={e => onInputChange(e)}
                                    />
                                    <label className="custom-file-label" htmlFor="inputGroupFile02" aria-describedby="imageHelp">Choose image</label>
                                </div>
                            </div>

                        </div>
                        <div className="form-group">
                            <label htmlFor="productName">Giá sản phẩm</label>
                            <input type="text" placeholder="Product Price " name="price" value={price} onChange={e => onInputChange(e)} className="form-control" />
                        </div>
                        <button className="btn btn-primary">Cập nhật</button>


                    </form>
                </div>
            </div>

        </div>
    )
}

EditProduct.propTypes = {
    products: PropTypes.array
}

export default EditProduct
