import React, { useState } from 'react';
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import dataFake from './dataFake';
import Routers from './routers'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import AddProduct from './pages/views/Admin/AddProduct';
// import AddProduct from './components/AddProduct';
function App() {

  const [products, setProducts] = useState(dataFake);
  const [status, setStatus] = useState(false);
  const [color, setColor] = useState('green');
  const onHandleClick = () => {
    // setStatus(true);
    setColor('red');
  }
  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProducts(newProducts);
  }
  const onHanleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product
    }
    setProducts([
      ...products,
      newProduct
    ])
  }
  return (
    <Routers>
    <div className="App">
    <Switch>

                <Route path="admin/Products">
                  <products products={products} onRemove={onHandleRemove} />
                </Route>
                <Route path="/product/add">
                  <AddProduct onAdd={onHanleAdd}/>
                </Route>
              </Switch>
      {/* <Routers products={products} onRemove={onHandleRemove} /> */}
    </div>
    </Routers>
  )

}
export default App;
