import React, { useState } from 'react';
// import Products from './components/Products';
// import Box from './components/Box/Box';
// import Home from './components/Main/NavBar'
// import Product from './components/Product';
import dataFake from './dataFake';
import Routers from './routers'
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link
} from "react-router-dom";
import AddProduct from './pages/views/Admin/AddProduct';
import ProductsManager from './pages/views/Admin/Products';
// import AddProduct from './components/AddProduct';
function App() {

  const [products, setProducts] = useState(dataFake);
  const [status, setStatus] = useState(false);
  const onHandleUpdate = (updateProduct) => {
    const newProducts = products.map(product => (
      product.id === updateProduct.id ? updateProduct : product  // Nếu product.id bằng với id của sản phẩm vừa chỉnh sửa thì trả về mảng có object mới
    ));
    localStorage.setItem('products', JSON.stringify(newProducts))
    setProducts(newProducts);
  }
  const onHandleRemove = (id) => {
    const newProducts = products.filter(product => product.id !== id);
    setProducts(newProducts);
  }
  const onHanleAdd = (product) => {
    const newProduct = {
      id: products.length + 1,
      ...product
    }
    setProducts([
      ...products,
      newProduct
    ])
  }

  return (

      <div className="App">
        {/* <Switch>

          <Route path="/admin/products">
            <ProductsManager products={products} onRemove={onHandleRemove} />
          </Route>
          <Route path="/product/add">
            <AddProduct onAdd={onHanleAdd} />
          </Route>
        </Switch> */}
        <Routers
        onAdd={onHanleAdd} 
        products={products}
        onRemove={onHandleRemove} 
        onUpdate={onHandleUpdate}/>


      </div>

  )

}
export default App;
