import React, { useState } from 'react'
import { useHistory } from "react-router-dom"
import { useForm } from "react-hook-form";

const Addproduct = ({ onAdd }) => {
    let history = useHistory();
    const [valueInput, setValueInput] = useState({});
    const onHandleChange = (e) => {
        const { name, value } = e.target;
        setValueInput(
            { ...valueInput, [name]: e.target.value }
        );
    }
    const onHandleSubmit = (e) => {
        e.preventDefault();
        onAdd(valueInput);
        history.push("/admin/products");
    };
    return (
        <div>
            <h3>Add New Product</h3>
            <form onSubmit={onHandleSubmit}>
                <div className="form-group" >
                    <label> Name:  </label>
                    <input type="text" className="form-control" placeholder="Name" onChange={onHandleChange} name="name" />
                </div>
                <div className="form-group">
                    <label>Imge </label>
                    <input type="text" className="form-control" placeholder="Image" onChange={onHandleChange} name="image" />
                </div>
                <div className="form-group">
                    <label>Price </label>
                    <input type="text" className="form-control" placeholder="Price" onChange={onHandleChange} name="price" />
                </div>
                <div className="form-group">
                    <input type="submit" value="Add Product" className="btn btn-primary" />
                </div>
            </form>
        </div>
    )
}
Addproduct.propTypes = {
}
export default Addproduct
